import urllib3, orjson


def printLyrics():
    artist=entry_1.get()
    song=entry_2.get()
    http=urllib3.PoolManager()
    request = http.request("GET",f'https://api.lyrics.ovh/v1/{artist}/{song}')
    lst=orjson.loads(request.data)["lyrics"]
    entry_3.config(text=lst, font=("Times 8"))
        
    





